import { Star, ExternalLink, Heart } from "lucide-react";
import { Button } from "./ui/button";
import { Card, CardContent } from "./ui/card";
import { Badge } from "./ui/badge";
import { ImageWithFallback } from "./figma/ImageWithFallback";
import { ShoppingResults } from "../type"; 

export function ProductCard({
  delivery,
  extracted_price,
  immersive_product_page_token,
  multiple_sources,
  position,
  price,
  product_id,
  product_link,
  rating,
  reviews,
  serpapi_immersive_product_api,
  serpapi_product_api,
  serpapi_thumbnails,
  snippet,
  source,
  source_icon,
  thumbnail,
  thumbnails,
  title,
  second_hand_condition,
  extensions,
  extracted_old_price,
  old_price,
  tag
}: ShoppingResults) {
  return (
    <Card className="group overflow-hidden border border-border hover:shadow-lg transition-shadow duration-200">
      <div className="relative">
        <ImageWithFallback
          src={thumbnail}
          alt={title}
          className="w-full h-48 object-scale-down"
        />
        {tag && (
          <Badge className="absolute top-2 left-2 bg-destructive text-destructive-foreground">
            {tag}
          </Badge>
        )}
        {/* <Button
          size="sm"
          variant="ghost"
          className="absolute top-2 right-2 h-8 w-8 p-0 bg-background/80 hover:bg-background opacity-0 group-hover:opacity-100 transition-opacity"
        >
          <Heart className="h-4 w-4" />
        </Button> */}
      </div>
      
      <CardContent className="p-4 space-y-3">
        
        {/* Product Name */}
        <h3 className="font-medium line-clamp-2 leading-5">{title}</h3>
        
        {/* Rating */}
        {rating > 0 && reviews > 0 && <div className="flex items-center gap-1">
          <div className="flex items-center">
            {[...Array(5)].map((_, i) => (
              <Star
                key={i}
                className={`h-4 w-4 ${
                  i < Math.floor(rating)
                    ? "fill-yellow-400 text-yellow-400"
                    : "text-gray-200"
                }`}
              />
            ))}
          </div>
          <span className="text-sm text-muted-foreground">
            {rating} ({reviews})
          </span>
        </div>}
        
        
        {/* Price */}
        <div className="flex items-center gap-2">
          <span className="text-base">{price}</span>
          {old_price && (
            <span className="text-sm text-muted-foreground line-through">
              {old_price}
            </span>
          )}
        </div>
        
        {/* Platform and Action */}
        <div className="flex items-center justify-between pt-2">
          <div className="flex items-center gap-2">
            <ImageWithFallback
              src={source_icon}
              alt={source}
              className="h-5 w-5 rounded"
            />
            <span className="text-sm text-muted-foreground">{source}</span>
          </div>
          <Button size="sm" className="gap-2 bg-primary hover:bg-primary/90">
            <ExternalLink className="h-3 w-3" />
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}
