import { useSelector, useDispatch } from "react-redux";
import { RootState } from "../../store/store";
import { useState } from "react";

export function FilterCheckedBar() {
  const dispatch = useDispatch();
  const checked_elements = useSelector(
    (state: RootState) => state.filter.checked_elements
  );

  // Local state to track clicks (optional, can be replaced with Redux)
  const [clicked, setClicked] = useState<{ [key: string]: boolean }>({});

  if (!checked_elements || checked_elements.length === 0) return null;

  const handleClick = (element) => {
    // toggle clicked state
    setClicked((prev) => ({
      ...prev,
      [element.text]: !prev[element.text],
    }));

    // TODO: dispatch action to remove from checked_elements if needed
  };

  return (
    <div className="flex flex-row flex-wrap gap-2 p-2 bg-[#f3f5f6] rounded mb-4">
      {checked_elements.map((element, idx) => (
        <button
          key={idx}
          onClick={() => handleClick(element)}
          className={`
            px-3 py-1 rounded-full text-sm font-medium
            transition-colors duration-200
            ${clicked[element.text] ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800"}
            hover:bg-blue-500 hover:text-white
          `}
        >
          {element.text}
        </button>
      ))}
    </div>
  );
}
