import { Star, ExternalLink, Package } from "lucide-react";
import { Button } from "./ui/button";
import { Card, CardContent } from "./ui/card";
import { Badge } from "./ui/badge";
import { ImageWithFallback } from "./figma/ImageWithFallback";
import { ShoppingResults } from "../type"; 

export function ProductCard({
  delivery,
  price,
  product_link,
  rating,
  reviews,
  source,
  source_icon,
  thumbnail,
  title,
  second_hand_condition,
  old_price,
  tag,
  snippet,
}: ShoppingResults) {
  return (
    <a href={product_link} target="_blank" rel="noopener noreferrer">
      <Card className="group overflow-hidden border border-border hover:shadow-lg transition-shadow duration-200">
        <div className="relative">
          <ImageWithFallback
            src={thumbnail}
            alt={title}
            className="w-full h-48 object-scale-down"
          />
          {tag && (
            <Badge className="absolute top-2 left-2 bg-destructive text-destructive-foreground">
              {tag}
            </Badge>
          )}
        </div>
        
        <div className="px-4 py-2 grid grid-rows-5 h-full">
          <h3 className="font-medium line-clamp-2 leading-5">{title}</h3>
          <div className="flex items-center">
            <span className="text-base">{price}</span>
            {old_price && (
              <span className="text-sm text-muted-foreground line-through">
                {old_price}
              </span>
            )}
          </div>

          <div className="flex items-center">
            {rating > 0 && reviews > 0 && (
              <div className="flex items-center gap-1">
                <div className="flex items-center">
                  {[...Array(5)].map((_, i) => (
                    <Star
                      key={i}
                      className={`h-4 w-4 ${
                        i < Math.floor(rating)
                          ? "fill-yellow-400 text-yellow-400"
                          : "text-gray-200"
                      }`}
                    />
                  ))}
                </div>
                <span className="text-sm text-muted-foreground">
                  ({reviews})
                </span>
              </div>
            )}
          </div>

          <div className="flex items-center">
            <div className="text-sm text-muted-foreground flex items-center gap-1">
              {delivery ? (
                <div className="flex gap-1 items-center">
                  <Package className="h-4 w-4" />
                  <span>{delivery}</span>
                </div>
              ) : (
                  snippet.split("(")[0].trim()
              )}
            </div>
          </div>

          <div className="flex items-center">
              <Badge variant="secondary" className="bg-yellow-100 text-yellow-800">
                {second_hand_condition || "Brand new"}
              </Badge>
          </div>
          <div className="flex items-center">
            <ImageWithFallback
              src={source_icon}
              alt={source}
              className="h-5 w-5 rounded"
            />
            <span className="text-sm text-muted-foreground">{source}</span>
            {/* <Button
              asChild
              size="sm"
              className="gap-2 bg-primary hover:bg-primary/90"
            >
              <a href={product_link} target="_blank" rel="noopener noreferrer">
                <ExternalLink className="h-3 w-3" />
              </a>
            </Button> */}
          </div>
        </div>
      </Card>
    </a>
  );
}
