import { ProductCard } from "./ProductCard";
import { Button } from "./ui/button";
import { Filter, SortAsc } from "lucide-react";
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { setProducts, setSearchParameters } from "../store/searchSlice";
import { setFilter } from "../store/filterSlice"
import { RootState } from "../store/store";
import { GoogleShopping, ProductCardProps, FilterElements, SearchParameters } from "../type";

export function ProductList(){
    const {
        products, 
        query,
    } = useSelector((state: RootState) => state.search);
    const {
        device,
        engine,
        gl,
        google_domain,
        hl,
        q,
        shoprs,
    }  = useSelector((state: RootState) => state.search.search_parameters);
    const dispatch = useDispatch();

    function dealData(rowData: GoogleShopping){
        const products: ProductCardProps[] = rowData.shopping_results?.map(item => ({
            id: item.product_id,
            name: item.title,
            price: item.price,
            originalPrice: item.old_price,
            brand: item.source,
            rating: item.rating,
            reviewCount: item.reviews,
            platform: item.source,
            platformLogo: item.source_icon,
            image: item.thumbnail
        })) || [];
        const search_parameters: SearchParameters = rowData.search_parameters;
        const filters: FilterElements[] = rowData.filters?.map(item => ({
            input_type: item.input_type,
            options: item.options,
        })) || [];
        dispatch(setProducts(products));
        dispatch(setSearchParameters(search_parameters));
        dispatch(setFilter(filters));
    }
    
    useEffect(() => {
        const controller = new AbortController();
        async function fetchData() {
            if(!query){
                return;
            }
            const params = new URLSearchParams({
                q: query,
                device,
                engine,
                gl,
                hl,
                google_domain,
                shoprs,
            });
            try {
                const res = await fetch(`http://localhost:8000/api/products?${params.toString()}`, {
                    signal: controller.signal,
                });
                const data = await res.json();
                dealData(data);
            } catch (err: any) {
                if (err.name !== "AbortError") {
                    console.error(err);
                }
            } 
        }

        fetchData();
        return () => controller.abort();
    }, [query]);
    
    return(
        <div className="p-5">
            {/* Header Section */}
            <div className="mb-6">
                <h1 className="mb-2">For You</h1>
                <p className="text-muted-foreground">
                Discover personalized deals and recommendations
                across all platforms
                </p>
            </div>

            {/* Filters and Sort */}
            <div className="flex items-center justify-between mb-6">

                <div className="flex items-center gap-3">
                <Button variant="outline" className="gap-2">
                    <Filter className="h-4 w-4" />
                    Filters
                </Button>
                <Button variant="outline" className="gap-2">
                    <SortAsc className="h-4 w-4" />
                    Sort by Price
                </Button>
                </div>
                <div className="text-sm text-muted-foreground">
                Showing {products.length} products
                </div>
            </div>

            {/* Products Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 mb-8">
                {products.map((product) => (
                    <ProductCard key={product.id} {...product} />
                ))}
            </div>

            {/* Load More */}
            <div className="flex justify-center">
                <Button variant="outline" className="px-8">
                Load More Products
                </Button>
            </div>
        </div>
    )
}
